s.x:
var $bm_rt;
var targetSize, realSize, x, x, y;
targetSize = 290;
realSize = thisLayer.sourceRectAtTime().width;
if (realSize > targetSize) {
    x = $bm_mul($bm_div(targetSize, realSize), 100);
} else {
    x = 100;
}
y = value[1];
$bm_rt = [
    x,
    y
];

v.x:
var $bm_rt;
var t1, t2;
t1 = key(1).time;
t2 = key(2).time;
value = Math.round(thisComp.layer('.textVariables #textVariables').text.sourceText.split(',')[0]);
$bm_rt = ease(time, t1, t2, 0, value);

c.x:
var $bm_rt;
var userColor, r, g, b;
userColor = thisComp.layer('.textVariables #textVariables').text.sourceText.split(',')[2];
r = $bm_div(parseInt(userColor.substr(0, 2), 16), 255);
g = $bm_div(parseInt(userColor.substr(2, 2), 16), 255);
b = $bm_div(parseInt(userColor.substr(4, 2), 16), 255);
$bm_rt = [
    r,
    g,
    b,
    1
];

